import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Todo } from "../app.model";
import { getDateYYYYMMDD } from "../app.util";

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.scss']
})
export class AddComponent {
  @Input() formState: "add" | "edit" = "add";
  @Input() formData: Todo = {
    id: null,
    name: "",
    description: "",
    dueDate: getDateYYYYMMDD(new Date()),
    priority: "normal",
    isDone: false,
  };
  @Output() submit = new EventEmitter<Todo>();
  shouldValidate = false;

  constructor() {
  }

  public getMinDate() {
    return getDateYYYYMMDD(new Date());
  }

  public onSubmit() {
    this.shouldValidate = true;
    if (!this.formData.name) return;
    this.submit.emit(this.formData);
    this.shouldValidate = false;
  }
}
<div class="add-container">
  <input
    [ngClass]="{'error': shouldValidate && !formData.name}"
    type="text"
    placeholder="Add new task..."
    [(ngModel)]="formData.name"
  />
  <div *ngIf="shouldValidate && !formData.name" class="error-text">This field is required</div>
  <label>Description</label>
  <textarea
    [(ngModel)]="formData.description"
  ></textarea>
  <div class="row">
    <div class="column date">
      <label>Due Date</label>
      <input
        type="date"
        [min]="getMinDate()"
        [(ngModel)]="formData.dueDate"
      />
    </div>
    <div class="column priority">
      <label>Priority</label>
      <select
        [(ngModel)]="formData.priority"
      >
        <option value="low">Low</option>
        <option value="normal">Normal</option>
        <option value="high">High</option>
      </select>
    </div>
  </div>
  <button (click)="onSubmit()">{{formState === "add" ? "Add" : "Update"}}</button>
</div>
