import { Component, Input, OnInit, Output, EventEmitter } from '@angular/core';
import { Todo } from "../app.model";

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.scss']
})
export class ListComponent implements OnInit {
  @Input() data: Todo[] = [];
  @Output() changeDone = new EventEmitter<Todo>();
  @Output() detail = new EventEmitter<Todo>();
  @Output() remove = new EventEmitter<number>();

  checkeds: number[] = [];

  searchText: string = "";

  constructor() {
  }

  public ngOnInit() {
  }

  public onChangeDone(data: Todo) {
    this.changeDone.emit(data);
  }

  public onDetail(data: Todo) {
    this.detail.emit(data);
  }

  public onRemove(index: number) {
    this.remove.emit(index);
  }

  public onChangeChecked(i: number) {
    if (this.checkeds.includes(i)) {
      this.checkeds = this.checkeds.filter(item => item !== i);
      return;
    }
    this.checkeds.push(i);
  }
}
<div class="list-container">
  <input
    type="text"
    placeholder="Search task by title..."
    [(ngModel)]="searchText"
  />

  <div *ngFor="let item of data; let i = index">
    <div class="item"
         [ngClass]="{'done': item.isDone }"
         *ngIf="item.name.includes(searchText)">
      <label class="checkbox-container">{{item.name}}
        <input
          type="checkbox"
          [value]="checkeds.includes(i)"
          (change)="onChangeChecked(i)"
        >
        <span class="checkmark"></span>
      </label>
      <div class="button-group" *ngIf="checkeds.includes(i)">
        <button class="done-btn" (click)="onChangeDone(item)">{{item.isDone ? "Undone" : "Done"}}</button>
        <button class="detail-btn" (click)="onDetail(item)">Detail</button>
        <button class="remove-btn" (click)="onRemove(i)">Remove</button>
      </div>
    </div>
  </div>
</div>
